#!/bin/bash

# üö® LIVEKIT SSL CERTIFICATE VERIFICATION SCRIPT üö®
# This script verifies and monitors SSL certificate status for all LiveKit domains

set -e

echo "üö® SSL CERTIFICATE VERIFICATION STARTING üö®"

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# LiveKit domains to check
DOMAINS=("lk.delo.sh" "lk-whip.delo.sh" "lk-turn.delo.sh")

# Function to check certificate status
check_certificate() {
    local domain=$1
    echo -e "${BLUE}üîç Checking certificate for $domain...${NC}"
    
    # Check if domain resolves
    if ! nslookup "$domain" >/dev/null 2>&1; then
        echo -e "${RED}‚ùå Domain $domain does not resolve${NC}"
        return 1
    fi
    
    # Check SSL certificate
    if curl -sSf --connect-timeout 10 -I "https://$domain" >/dev/null 2>&1; then
        # Get certificate info
        cert_info=$(openssl s_client -connect "$domain:443" -servername "$domain" 2>/dev/null </dev/null | openssl x509 -noout -dates 2>/dev/null)
        if [ $? -eq 0 ] && echo "$cert_info" | grep -q "notAfter"; then
            expiry=$(echo "$cert_info" | grep "notAfter" | cut -d= -f2)
            echo -e "${GREEN}‚úÖ $domain has valid SSL certificate (expires: $expiry)${NC}"
            return 0
        fi
    fi
    
    # Check if it's a self-signed certificate
    if openssl s_client -connect "$domain:443" -servername "$domain" 2>/dev/null </dev/null | openssl x509 -noout -issuer 2>/dev/null | grep -qi "self"; then
        echo -e "${YELLOW}‚ö†Ô∏è $domain has SELF-SIGNED certificate (needs proper Let's Encrypt cert)${NC}"
        return 2
    fi
    
    echo -e "${RED}‚ùå $domain SSL certificate check failed${NC}"
    return 1
}

# Function to check Traefik ACME status
check_acme_status() {
    echo -e "${BLUE}üîç Checking Traefik ACME certificate status...${NC}"
    
    if [ -f "/home/delorenj/docker/trunk-main/core/traefik/traefik-data/acme.json" ]; then
        # Count certificates in ACME storage
        cert_count=$(jq -r '.letsencrypt.Certificates | length' /home/delorenj/docker/trunk-main/core/traefik/traefik-data/acme.json 2>/dev/null || echo "0")
        echo -e "${BLUE}üìã ACME storage contains $cert_count certificates${NC}"
        
        if [ "$cert_count" -gt 0 ]; then
            # List certificate domains
            echo -e "${BLUE}üìã Certificate domains in ACME storage:${NC}"
            jq -r '.letsencrypt.Certificates[].domain.main' /home/delorenj/docker/trunk-main/core/traefik/traefik-data/acme.json 2>/dev/null | while read domain; do
                echo -e "   - ${GREEN}$domain${NC}"
            done
        fi
    else
        echo -e "${YELLOW}‚ö†Ô∏è ACME storage file not found${NC}"
    fi
}

# Function to monitor certificate generation
monitor_certificate_generation() {
    echo -e "${BLUE}üîç Monitoring Traefik logs for certificate generation...${NC}"
    
    # Get recent Traefik logs related to certificates
    echo -e "${BLUE}Recent Traefik certificate activity:${NC}"
    docker logs traefik --since 5m 2>&1 | grep -i "certificate\|acme\|letsencrypt\|cloudflare" | tail -10 | while read line; do
        if echo "$line" | grep -qi "error"; then
            echo -e "${RED}‚ùå $line${NC}"
        elif echo "$line" | grep -qi "success\|obtained\|issued"; then
            echo -e "${GREEN}‚úÖ $line${NC}"
        else
            echo -e "${YELLOW}üìã $line${NC}"
        fi
    done
}

# Function to test WebRTC connectivity
test_webrtc_connectivity() {
    echo -e "${BLUE}üîç Testing WebRTC connectivity...${NC}"
    
    # Test LiveKit server health endpoint
    if curl -sSf --connect-timeout 10 "https://lk.delo.sh/rtc/validate" >/dev/null 2>&1; then
        echo -e "${GREEN}‚úÖ LiveKit server health check passed${NC}"
    else
        echo -e "${YELLOW}‚ö†Ô∏è LiveKit server health check failed - server may still be starting${NC}"
    fi
    
    # Test TURN server TLS
    echo -e "${BLUE}üîç Testing TURN server TLS on port 5349...${NC}"
    if timeout 5 openssl s_client -connect lk-turn.delo.sh:5349 -servername lk-turn.delo.sh </dev/null 2>/dev/null | grep -q "CONNECTED"; then
        echo -e "${GREEN}‚úÖ TURN server TLS connection successful${NC}"
    else
        echo -e "${YELLOW}‚ö†Ô∏è TURN server TLS connection failed${NC}"
    fi
}

# Function to force certificate renewal
force_certificate_renewal() {
    echo -e "${BLUE}üîß Forcing certificate renewal for domains with issues...${NC}"
    
    # Clear ACME cache to force renewal
    echo '{"letsencrypt": {"Account": {"Email": "delorenj@delo.sh", "Registration": {"body": {"status": "valid"}, "uri": "https://acme-v02.api.letsencrypt.org/acme/acct/2634568177"}, "PrivateKey": "", "KeyType": "4096"}, "Certificates": null}}' > /home/delorenj/docker/trunk-main/core/traefik/traefik-data/acme.json
    
    # Restart Traefik to trigger fresh certificate requests
    echo -e "${YELLOW}Restarting Traefik to trigger certificate renewal...${NC}"
    cd /home/delorenj/docker/trunk-main/core/traefik
    docker compose restart traefik
    
    echo -e "${GREEN}‚úÖ Certificate renewal initiated${NC}"
}

# Main execution
echo "========================================="
echo -e "${BLUE}üîç STEP 1: Checking current certificate status${NC}"
echo "========================================="

certificate_issues=0
for domain in "${DOMAINS[@]}"; do
    if ! check_certificate "$domain"; then
        ((certificate_issues++))
    fi
done

echo ""
echo "========================================="
echo -e "${BLUE}üîç STEP 2: Checking Traefik ACME status${NC}"
echo "========================================="

check_acme_status

echo ""
echo "========================================="
echo -e "${BLUE}üîç STEP 3: Monitoring certificate generation${NC}"
echo "========================================="

monitor_certificate_generation

echo ""
echo "========================================="
echo -e "${BLUE}üîç STEP 4: Testing WebRTC connectivity${NC}"
echo "========================================="

test_webrtc_connectivity

echo ""
echo "========================================="
echo -e "${BLUE}üìã SUMMARY REPORT${NC}"
echo "========================================="

if [ $certificate_issues -eq 0 ]; then
    echo -e "${GREEN}üéâ SUCCESS! All LiveKit domains have valid SSL certificates!${NC}"
    echo -e "${GREEN}üöÄ WebRTC should work properly with secure connections${NC}"
else
    echo -e "${YELLOW}‚ö†Ô∏è $certificate_issues domain(s) have certificate issues${NC}"
    echo -e "${BLUE}Certificate generation may still be in progress...${NC}"
    
    echo ""
    echo -e "${BLUE}üîß Would you like to force certificate renewal? (y/N)${NC}"
    read -r response
    if [[ "$response" =~ ^[Yy]$ ]]; then
        force_certificate_renewal
        echo ""
        echo -e "${BLUE}Please wait 2-3 minutes and run this script again to check certificate status.${NC}"
    fi
fi

echo ""
echo -e "${BLUE}üîß Useful Commands:${NC}"
echo "- Check Traefik logs: docker logs traefik --tail 50"
echo "- Check LiveKit logs: docker logs livekit-server --tail 20"
echo "- Test SSL manually: curl -I https://lk.delo.sh"
echo "- Monitor certificates: watch 'curl -I https://lk.delo.sh 2>&1 | head -5'"

echo ""
echo "üö® SSL CERTIFICATE VERIFICATION COMPLETE üö®"
EOF